package com.intellectualcrafters.plot;

import org.bukkit.block.Biome;

/**
 * plot settings
 * @author Citymonstret
 *
 */
public class PlotSettings {

    private String alias;
	/**
	 * plot object
	 */
	@SuppressWarnings("unused")
	private Plot plot;
	/**
	 * plot biome
	 */
	private Biome biome;
    /**
     * plot rain
     */
    private boolean rain;
	/**
	 * plot time
	 */
	private Long time;
	/**
	 * Change time?
	 */
	private boolean changeTime;
	private PlotHomePosition position;
	/**
	 * Constructor
	 * @param plot
	 */
	public PlotSettings(Plot plot) {
		this.plot = plot;
        this.alias = "";
	}
	
	/**
	 * 
	 * @param b
	 */
	public void setBiome(Biome b) {
		this.biome = b;
	}
	
	/**
	 * 
	 * @param b
	 */
	public void setTimeChange(boolean b) {
		this.changeTime = b;
	}
	
	/**
	 * 
	 * @param l
	 */
	public void setTime(long l) {
		this.time = l;
	}
	
	/**
	 * 
	 * @return
	 * @deprecated
	 */
	public Biome getBiome() {
		return this.biome;
	}
	
	/**
	 * 
	 * @return
	 */
	public boolean getRain() {
		return this.rain;
	}

    public void setRain(boolean b) {
        this.rain = b;
    }

	/**
	 * 
	 * @return
	 */
	public long getTime() {
		return this.time;
	}
	
	/**
	 * 
	 * @return
	 */
	public boolean getChangeTime() {
		return this.changeTime;
	}

    /**
     *
     */
    public  void setAlias(String alias) {
        this.alias = alias;
    }

    public PlotHomePosition getPosition() { return this.position; }
    public void setPosition(PlotHomePosition position) { this.position = position; }
    public String getAlias() { return this.alias; }
    public String getJoinMessage() { return ""; }

    public String getLeaveMessage() { return ""; }
}
